                    <div className='content' key={account.id}>
                        <h1>account  user  {account.id} </h1>
                        <h2>Matricule : user-000 {account.id} </h2>
                        <h4>Name : {account.name}</h4>
                        <h4>Last Name : {account.lastName}</h4>
                        <h4>Email : {account.name}{account.lastName}{account.id}@user.com</h4>
                        <h4>Name : {account.section}</h4>
                        <button onClick={()=> dispatch(deleteUser({id: account.id}))}>Delete</button>
