import { createSlice } from "@reduxjs/toolkit";

const taskSlice = createSlice({
  name: "Tasks",
  initialState: {
    Tasks: [],
  },
  reducers: {
    addTasks: (state, action) => {
      state.Tasks.push(action.payload);
    },
    doneTask: (state, action) => {
      state.items.globalList.map((item) =>
        item.id === action.payload ? (item.isdone = !item.isdone) : item
      );
      state.items.filtredList = state.items.globalList;
    },

    editTasks: (state, action) => {
      state.Tasks.map((task) => {
        if (task.id === action.payload.id) {
          task.description = action.payload.description;
        }
      });
    },
    filterByDone: (state, action) => {
      state.items.filtredList = state.items.globalList.filter(
        (task) => task.isdone === action.payload
      );
    },
    reset: (state) => {
      state.items.filtredList = state.items.globalList;
    },
  },
});
export const { addTasks, editTasks, filterByDone ,} = taskSlice.actions;
export default taskSlice.reducer;
