import React, { useState } from 'react';
import AddTask from './components/AddTask';
import ListTask from './components/ListTask';

const App = () => {
  const [tasks, setTasks] = useState([
    { id: 1, description: 'Task 1', isDone: false },
    { id: 2, description: 'Task 2', isDone: true },
    { id: 3, description: 'Task 3', isDone: false },
  ]);
  const [filter, setFilter] = useState('all');

  
  return (
    <div>
      <AddTask />
      <div>
        <button onClick={() => setFilter('all')}>All</button>
        <button onClick={() => setFilter('done')}>Done</button>
        <button onClick={() => setFilter('not')}>Not Done</button>
      </div>
      <ListTask tasks={tasks} filter={filter} onEdit={editTasks} onDelete={deleteTasks} />
    </div>
  );
};

export default App;
