import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { addTasks } from '../redux/taskSlice'

export default function AddTask() {
  const [description, setDescription] = useState('')
  const dispatch = useDispatch()
  const item = useSelector((state)=>state.Tasks.Tasks)
  const [done , setDone] = useState(false)

  return (
    <div className='task-top'>
      <div className='task'>
        <span>Tasks</span>
        <input type='text' value={description} onChange={(e)=>setDescription(e.target.value)}/>
        <button onClick={()=>
          dispatch(addTasks({id : item.length+1 , description}))
         } >Add</button>
      </div>
      <div >
        {item.length > 0 ? item.map(task=>
         <div className='content' key={task.id}>
          <span></span>
          <span> Description : {task.description} {task.id}</span>
          <button>Edit</button>
          <button onClick={()=>
            dispatch({id:task.id}) 
        }
          >Delete</button>
          <input type="checkbox" checked/>


         </div>
         
        )
         : <h6>No Tasks</h6>}
      </div>
      
    </div>
  )
}
