import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import {} from '../redux/taskSlice'

export default function Task() {
    const [title, setTitle] =useState('');
    const [description, setDescription] =useState('');
    const dispatch =useDispatch();
    const items = useSelector((state)=>state.Task);
  return (
    <div className='container'>
      
        <div className='card'>
            <input type='text' placeholder="your item title" onChange={(e)=>setTitle(e.target.value)}/>
            <input type='text' placeholder="your item  description" onChange={(e)=>setDescription(e.target.value)}/>
            <button 
             //</div>onClick={()=></div>dispatch(addItem({ id: items.length+1, title, description}))}
             > Add Item </button>
        </div>
        <div className='items'>
            {items.length > 0 ? items.map((item) => 
                <div className='item' key={item.id}>
                    <h3>{item.title}</h3>
                    <p>{item.description}</p>
                    <button >//onClick={()=> dispatch(deleteItem({id : item.id}))}>Delete</button>
                    <button>Edit</button>
                </div> 
            )
            :<h6>no items</h6>}
               
        </div>
    </div>
  )
}
