import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import AddTask from './AddTask';


const Task = () => {
const [filter , setFilter] = useState('')
const [description, setDescription] = useState("");
const [done, steDone] = useState('')
const item = useSelector((state)=>state.newTask.Tasks);
const dispatch = useDispatch();




  return (
    <div>
      <form>
        <input type="text" value={description} onChange={(e) => setDescription(e.target.value)} />
        <button onClick={()=>{
          dispatch(AddTask({id:item.length+1,description}))
          setDescription('')
        }
        }>Add</button>
        <div>
        {item.length > 0  ? item.map((task) =>
            <div className='content' key={task.id}>
              <ListTask/>
            </div>
        )
      : <h2> fddeeewffd</h2>}
        </div>
      </form>
      <button >Edit</button>
      <button>Delete</button>
      <input type="checkbox" checked={done} />
      <div>
        <button onClick={() => setFilter("all")}>All</button>
        <button onClick={() => setFilter("done")}>Done</button>
        <button onClick={() => setFilter("not")}>Not Done</button>
      </div>
    </div>
  );
};

export default Task;
