import React from 'react';
import Task from './Task';
const [filter, setFilter] = useState('all');

const ListTask = ({ tasks, filter, onEdit, onDelete }) => {
  const filteredTasks = filter === 'done' ? tasks.filter(task => task.isDone) :
    filter === 'not' ? tasks.filter(task => !task.isDone) : tasks;

  return (
    <div>
      {filteredTasks.map(task =>
        <Task key={task.id} {...task} onEdit={onEdit} onDelete={onDelete} />
      )}
    </div>
  );
};

export default ListTask;
